import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';

function IndustryNewsDetail() {
    const { id } = useParams();
    const [newsArticle, setNewsArticle] = useState(null);
    const [error, setError] = useState('');
    const [username, setUsername] = useState('');
    const [commentText, setCommentText] = useState('');
    const [comments, setComments] = useState([]);

    useEffect(() => {
        const fetchNewsArticle = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/news/${id}`);
                setNewsArticle(response.data);
                setComments(response.data.comments || []);
            } catch (error) {
                setError('Error fetching the news article');
            }
        };
        fetchNewsArticle();
    }, [id]);

    // Handle comment submission
    const handleCommentSubmit = async (e) => {
        e.preventDefault();
        const newComment = {
            user: username,
            text: commentText,
            time: new Date().toLocaleString(),
        };
        setComments((prevComments) => [newComment, ...prevComments]);

        try {
            await axios.post(`http://localhost:5000/api/news/${id}/comment`, newComment);
            setCommentText('');
        } catch (error) {
            console.error('Error posting the comment:', error);
        }
    };

    if (error) {
        return <p>{error}</p>;
    }

    if (!newsArticle) {
        return <p>Loading...</p>;
    }

    return (
        <div className="container mt-5">
            <h1>{newsArticle.title}</h1>
            <p className="text-muted">{newsArticle.datePosted}</p>
            <img src={newsArticle.imageUrl} alt={newsArticle.title} className="img-fluid mb-4 rounded" />
            <div className="content mb-5">
                {newsArticle.content.split('\n').map((line, index) => (
                    <p key={index}>{line}</p>
                ))}
            </div>

            {/* Display Industry Information */}
            {newsArticle.industry && newsArticle.industry.length > 0 && (
                <div className="related-industry mb-5">
                    <h4>Industries</h4>
                    <ul className="list-inline">
                        {newsArticle.industry.map((sector, index) => (
                            <li key={index} className="list-inline-item">
                                <span className="badge badge-info">{sector}</span>
                            </li>
                        ))}
                    </ul>
                </div>
            )}

            {/* Comments Section */}
            <h3>Comments</h3>
            <ul className="list-unstyled">
                {comments.map((comment, index) => (
                    <li key={index}>
                        <strong>{comment.user}</strong>: {comment.text} <span className="text-muted">({comment.time})</span>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default IndustryNewsDetail;
