// client/src/pages/AddCompanyInfoPage.js
import React, { useState } from 'react';
import axios from 'axios';

function AddCompanyInfoPage() {
  const [company, setCompany] = useState({
    name: '',
    phone: '',
    email: '',
    location: '',
    background: '',
    mission: '',
    benefits: '',
    culture: '',
    testimonials: '',
    image: '',
    video: ''
  });

  const handleChange = (e) => {
    setCompany({ ...company, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/companies/add', company);
      alert('Company information added successfully!');
    } catch (error) {
      console.error('There was an error adding the company info', error);
    }
  };

  return (
    <div>
      <h1>Add Company Info</h1>
      <form onSubmit={handleSubmit}>
        <input name="name" value={company.name} onChange={handleChange} placeholder="Company Name" />
        <input name="phone" value={company.phone} onChange={handleChange} placeholder="Phone" />
        <input name="email" value={company.email} onChange={handleChange} placeholder="Email" />
        <input name="location" value={company.location} onChange={handleChange} placeholder="Location" />
        <textarea name="background" value={company.background} onChange={handleChange} placeholder="Company Background" />
        <textarea name="mission" value={company.mission} onChange={handleChange} placeholder="Company Mission" />
        <textarea name="benefits" value={company.benefits} onChange={handleChange} placeholder="Benefits" />
        <textarea name="culture" value={company.culture} onChange={handleChange} placeholder="Culture" />
        <textarea name="testimonials" value={company.testimonials} onChange={handleChange} placeholder="Testimonials" />
        <input name="image" value={company.image} onChange={handleChange} placeholder="Image URL" />
        <input name="video" value={company.video} onChange={handleChange} placeholder="Video URL" />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default AddCompanyInfoPage;
