import React, { useState } from "react";
import { Container, Form, Button, Alert } from "react-bootstrap";
import jsPDF from "jspdf";
import "jspdf-autotable"; // Import autoTable plugin for better table handling

function ResumeBuilderPage() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    education: "",
    experience: "",
    skills: "",
  });
  const [success, setSuccess] = useState("");

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const generatePDF = () => {
    const doc = new jsPDF();

    // Add header
    doc.setFontSize(22);
    doc.setFont("helvetica", "bold");
    doc.text(formData.name, 14, 22); // Name
    doc.setFont("helvetica", "normal");
    doc.setFontSize(12);
    doc.text(formData.email, 14, 32); // Email
    doc.text(formData.phone, 14, 38); // Phone
    doc.line(10, 40, 200, 40); // Underline

    // Add sections
    const addSection = (title, content, startY) => {
      doc.setFontSize(16);
      doc.setFont("helvetica", "bold");
      doc.text(title, 14, startY);
      doc.setFontSize(12);
      doc.setFont("helvetica", "normal");
      doc.text(content, 14, startY + 8);
      doc.line(10, startY + 12, 200, startY + 12); // Section underline
    };

    // Education
    addSection("Education", formData.education, 50);

    // Experience
    addSection("Experience", formData.experience, 80);

    // Skills
    addSection("Skills", formData.skills, 110);

    // Save the PDF
    doc.save(`${formData.name}_Resume.pdf`);
    setSuccess("Your resume has been generated!");
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    generatePDF();
  };

  return (
    <Container className="mt-5">
      <h2>Create Your Resume</h2>
      {success && <Alert variant="success">{success}</Alert>}
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="formName" className="mb-3">
          <Form.Label>Name</Form.Label>
          <Form.Control
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            placeholder="Enter your name"
            required
          />
        </Form.Group>

        <Form.Group controlId="formEmail" className="mb-3">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="Enter your email"
            required
          />
        </Form.Group>

        <Form.Group controlId="formPhone" className="mb-3">
          <Form.Label>Phone</Form.Label>
          <Form.Control
            type="text"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            placeholder="Enter your phone number"
            required
          />
        </Form.Group>

        <Form.Group controlId="formEducation" className="mb-3">
          <Form.Label>Education</Form.Label>
          <Form.Control
            as="textarea"
            name="education"
            value={formData.education}
            onChange={handleChange}
            placeholder="Enter your education details"
            required
          />
        </Form.Group>

        <Form.Group controlId="formExperience" className="mb-3">
          <Form.Label>Experience</Form.Label>
          <Form.Control
            as="textarea"
            name="experience"
            value={formData.experience}
            onChange={handleChange}
            placeholder="Enter your work experience"
            required
          />
        </Form.Group>

        <Form.Group controlId="formSkills" className="mb-3">
          <Form.Label>Skills</Form.Label>
          <Form.Control
            as="textarea"
            name="skills"
            value={formData.skills}
            onChange={handleChange}
            placeholder="Enter your skills"
            required
          />
        </Form.Group>

        <Button variant="primary" type="submit">
          Generate Resume
        </Button>
      </Form>
    </Container>
  );
}

export default ResumeBuilderPage;
