import React, { useState } from "react";
import { Container, Form, Button, Alert } from "react-bootstrap";
import jsPDF from "jspdf";
import "jspdf-autotable";

function ResumeBuilderPage() {
  const initialFormData = {
    name: "",
    email: "",
    phone: "",
    education: [],
    experience: [],
    skills: [],
    references: [], // Add references field
  };

  const [formData, setFormData] = useState(initialFormData);
  const [success, setSuccess] = useState("");

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handlePhoneChange = (e) => {
    const value = e.target.value.replace(/\D/g, ""); // Allow only digits
    setFormData({ ...formData, phone: value });
  };

  const addEducation = () => {
    setFormData((prevData) => ({
      ...prevData,
      education: [...prevData.education, ""],
    }));
  };

  const handleEducationChange = (index, value) => {
    const updatedEducation = [...formData.education];
    updatedEducation[index] = value;
    setFormData({ ...formData, education: updatedEducation });
  };

  const addExperience = () => {
    setFormData((prevData) => ({
      ...prevData,
      experience: [...prevData.experience, ""],
    }));
  };

  const handleExperienceChange = (index, value) => {
    const updatedExperience = [...formData.experience];
    updatedExperience[index] = value;
    setFormData({ ...formData, experience: updatedExperience });
  };

  const addSkill = () => {
    setFormData((prevData) => ({
      ...prevData,
      skills: [...prevData.skills, ""],
    }));
  };

  const handleSkillChange = (index, value) => {
    const updatedSkills = [...formData.skills];
    updatedSkills[index] = value;
    setFormData({ ...formData, skills: updatedSkills });
  };

  const addReference = () => {
    setFormData((prevData) => ({
      ...prevData,
      references: [...prevData.references, ""],
    }));
  };

  const handleReferenceChange = (index, value) => {
    const updatedReferences = [...formData.references];
    updatedReferences[index] = value;
    setFormData({ ...formData, references: updatedReferences });
  };

  const generatePDF = () => {
    const doc = new jsPDF();
    doc.setFontSize(22);
    doc.setFont("helvetica", "bold");
    doc.text(formData.name, 14, 22);
    doc.setFont("helvetica", "normal");
    doc.setFontSize(12);
    doc.text(formData.email, 14, 32);
    doc.text(formData.phone, 14, 38);
    doc.line(10, 40, 200, 40);

    const addSection = (title, content, startY) => {
      doc.setFontSize(16);
      doc.setFont("helvetica", "bold");
      doc.text(title, 14, startY);
      doc.setFontSize(12);
      doc.setFont("helvetica", "normal");
      content.forEach((item, index) => {
        doc.text(`â€¢ ${item}`, 14, startY + 8 + index * 6);
      });
      doc.line(
        10,
        startY + 8 + content.length * 6,
        200,
        startY + 8 + content.length * 6
      );
    };

    addSection("Education", formData.education, 50);
    addSection("Experience", formData.experience, 80);
    addSection("Skills", formData.skills, 130);
    addSection("References", formData.references, 160); // Add references section

    doc.save(`${formData.name}_Resume.pdf`);
    setSuccess("Your resume has been generated!");
    setFormData(initialFormData);

    setTimeout(() => {
      setSuccess("");
    }, 2000);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    generatePDF();
  };

  return (
    <Container
      className="mt-5"
      style={{ position: "relative", paddingBottom: "60px" }}
    >
      <h2>Create Your Resume</h2>
      {success && <Alert variant="success">{success}</Alert>}
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="formName" className="mb-3">
          <Form.Label>Name</Form.Label>
          <Form.Control
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            placeholder="Enter your name"
            required
          />
        </Form.Group>

        <Form.Group controlId="formEmail" className="mb-3">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="Enter your email"
            required
          />
        </Form.Group>

        <Form.Group controlId="formPhone" className="mb-3">
          <Form.Label>Phone</Form.Label>
          <Form.Control
            type="text"
            name="phone"
            value={formData.phone}
            onChange={handlePhoneChange} // Use phone change handler
            placeholder="Enter your phone number"
            required
          />
        </Form.Group>

        <Form.Label>Education</Form.Label>
        {formData.education.map((edu, index) => (
          <Form.Group
            controlId={`formEducation${index}`}
            className="mb-3"
            key={index}
          >
            <Form.Control
              type="text"
              value={edu}
              onChange={(e) => handleEducationChange(index, e.target.value)}
              placeholder="Enter your education"
              required
            />
          </Form.Group>
        ))}
        <div
          className="d-flex align-items-center mb-4"
          style={{ justifyContent: "flex-end" }}
        >
          <Button variant="primary" onClick={addEducation}>
            + Add Education
          </Button>
        </div>

        <Form.Label>Experience</Form.Label>
        {formData.experience.map((exp, index) => (
          <Form.Group
            controlId={`formExperience${index}`}
            className="mb-3"
            key={index}
          >
            <Form.Control
              type="text"
              value={exp}
              onChange={(e) => handleExperienceChange(index, e.target.value)}
              placeholder="Enter your experience"
              required
            />
          </Form.Group>
        ))}
        <div
          className="d-flex align-items-center mb-4"
          style={{ justifyContent: "flex-end" }}
        >
          <Button variant="primary" onClick={addExperience}>
            + Add Experience
          </Button>
        </div>

        <Form.Label>Skills</Form.Label>
        {formData.skills.map((skill, index) => (
          <Form.Group
            controlId={`formSkill${index}`}
            className="mb-3"
            key={index}
          >
            <Form.Control
              type="text"
              value={skill}
              onChange={(e) => handleSkillChange(index, e.target.value)}
              placeholder="Enter your skill"
              required
            />
          </Form.Group>
        ))}
        <div
          className="d-flex align-items-center mb-4"
          style={{ justifyContent: "flex-end" }}
        >
          <Button variant="primary" onClick={addSkill}>
            + Add Skill
          </Button>
        </div>

        <Form.Label>References</Form.Label>
        {formData.references.map((ref, index) => (
          <Form.Group
            controlId={`formReference${index}`}
            className="mb-3"
            key={index}
          >
            <Form.Control
              type="text"
              value={ref}
              onChange={(e) => handleReferenceChange(index, e.target.value)}
              placeholder="Enter reference"
              required
            />
          </Form.Group>
        ))}
        <div
          className="d-flex align-items-center mb-4"
          style={{ justifyContent: "flex-end" }}
        >
          <Button variant="primary" onClick={addReference}>
            + Add Reference
          </Button>
        </div>

        <Button type="submit" variant="success">
          Generate PDF
        </Button>
      </Form>
    </Container>
  );
}

export default ResumeBuilderPage;
