import React, { useState, useEffect } from "react";
import { Container, Row, Col } from "react-bootstrap";
import BookmarksPriority from "./BookmarksPriority";

const BookmarkPage = () => {
  const [bookmarkedJobs, setBookmarkedJobs] = useState([]);
  const [categories, setCategories] = useState({}); // Store job categories
  const [appliedCategory, setAppliedCategory] = useState("All"); // Default to "All"

  useEffect(() => {
    // Fetch bookmarked jobs and their categories
    const fetchBookmarkedJobs = async () => {
      // Replace this with your actual API call
      const jobs = await fetch("/api/bookmarkedJobs").then((res) => res.json());
      setBookmarkedJobs(jobs);

      // Fetch categories for the jobs
      const jobCategories = {};
      jobs.forEach((job) => {
        jobCategories[job._id] = job.category || ""; // Assign categories
      });
      setCategories(jobCategories);
    };

    fetchBookmarkedJobs();
  }, []);

  const handleApplyFilter = (selectedCategory) => {
    setAppliedCategory(selectedCategory); // Update the applied category
  };

  // Filter jobs based on the applied category
  const filteredJobs = bookmarkedJobs.filter((job) => {
    if (appliedCategory === "All") {
      return true; // Show all jobs
    }

    if (appliedCategory === "Uncategorized") {
      return !categories[job._id]; // No category assigned
    }

    return categories[job._id] === appliedCategory; // Match the applied category
  });

  return (
    <Container>
      <BookmarksPriority
        selectedCategory={appliedCategory}
        onApplyFilter={handleApplyFilter}
      />
      <Row>
        {filteredJobs.map((job) => (
          <Col key={job._id} md={4}>
            <div className="job-card">
              <h4>{job.title}</h4>
              <p>{job.description}</p>
              <div className="d-flex justify-content-between">
                <button className="btn btn-primary">View Details</button>
                <button className="btn btn-danger">Remove Bookmark</button>
              </div>
            </div>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default BookmarkPage;
