import React, { useState } from "react";
import { Container, Row, Col, Button, Alert } from "react-bootstrap";

const initialBoard = Array(9).fill(null);

const LeisurePage = () => {
  const [board, setBoard] = useState(initialBoard);
  const [isGameOver, setIsGameOver] = useState(false);
  const [winner, setWinner] = useState(null);

  const checkWinner = (squares) => {
    const lines = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6],
    ];

    for (let line of lines) {
      const [a, b, c] = line;
      if (
        squares[a] &&
        squares[a] === squares[b] &&
        squares[a] === squares[c]
      ) {
        return squares[a];
      }
    }

    return squares.every((square) => square) ? "Draw" : null; // Check for draw
  };

  const handleUserMove = (index) => {
    if (board[index] || isGameOver) return;

    const newBoard = [...board];
    newBoard[index] = "X";
    setBoard(newBoard);
    const result = checkWinner(newBoard);

    if (result) {
      setWinner(result);
      setIsGameOver(true);
      return;
    }

    computerMove(newBoard);
  };

  const computerMove = (newBoard) => {
    const availableMoves = newBoard
      .map((val, index) => (val === null ? index : null))
      .filter((val) => val !== null);
    if (availableMoves.length > 0) {
      const randomIndex = Math.floor(Math.random() * availableMoves.length);
      newBoard[availableMoves[randomIndex]] = "O";
      setBoard(newBoard);
      const result = checkWinner(newBoard);

      if (result) {
        setWinner(result);
        setIsGameOver(true);
      }
    }
  };

  const resetGame = () => {
    setBoard(initialBoard);
    setIsGameOver(false);
    setWinner(null);
  };

  return (
    <Container className="mt-5">
      <Row className="justify-content-center">
        <Col xs={12} md={8} lg={6}>
          <h2 className="text-center mb-4">Tic Tac Toe</h2>
          {winner && (
            <Alert variant={winner === "Draw" ? "warning" : "success"}>
              {winner === "Draw" ? "It's a Draw!" : `Winner: ${winner}`}
            </Alert>
          )}
          <div
            className="d-flex flex-wrap justify-content-center"
            style={{ width: "300px", margin: "auto" }}
          >
            {board.map((square, index) => (
              <Button
                key={index}
                variant="outline-primary"
                className="m-1"
                style={{ width: "80px", height: "80px", fontSize: "24px" }}
                onClick={() => handleUserMove(index)}
              >
                {square}
              </Button>
            ))}
          </div>
          <Button variant="primary" className="mt-4 w-100" onClick={resetGame}>
            Restart Game
          </Button>
        </Col>
      </Row>
    </Container>
  );
};

export default LeisurePage;
