// ResumeBuilderPage.js
import React, { useState } from "react";
import { Container, Form, Button, Alert } from "react-bootstrap";
import jsPDF from "jspdf";
import "jspdf-autotable"; // Import autoTable plugin for better table handling

function ResumeBuilderPage() {
  const initialFormData = {
    name: "",
    email: "",
    phone: "",
    education: [],
    experience: "",
    skills: [],
  };

  const [formData, setFormData] = useState(initialFormData);
  const [success, setSuccess] = useState("");

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const addEducation = () => {
    setFormData((prevData) => ({
      ...prevData,
      education: [...prevData.education, ""],
    }));
  };

  const handleEducationChange = (index, value) => {
    const updatedEducation = [...formData.education]; // Create a copy of the current array
    updatedEducation[index] = value; // Update the specific index
    setFormData({ ...formData, education: updatedEducation }); // Set the state with the updated array
  };

  const addSkill = () => {
    setFormData((prevData) => ({
      ...prevData,
      skills: [...prevData.skills, ""],
    }));
  };

  const handleSkillChange = (index, value) => {
    const updatedSkills = [...formData.skills]; // Create a copy of the current array
    updatedSkills[index] = value; // Update the specific index
    setFormData({ ...formData, skills: updatedSkills }); // Set the state with the updated array
  };

  const generatePDF = () => {
    const doc = new jsPDF();

    // Add header
    doc.setFontSize(22);
    doc.setFont("helvetica", "bold");
    doc.text(formData.name, 14, 22); // Name
    doc.setFont("helvetica", "normal");
    doc.setFontSize(12);
    doc.text(formData.email, 14, 32); // Email
    doc.text(formData.phone, 14, 38); // Phone
    doc.line(10, 40, 200, 40); // Underline

    // Add sections
    const addSection = (title, content, startY) => {
      doc.setFontSize(16);
      doc.setFont("helvetica", "bold");
      doc.text(title, 14, startY);
      doc.setFontSize(12);
      doc.setFont("helvetica", "normal");
      content.forEach((item, index) => {
        doc.text(`â€¢ ${item}`, 14, startY + 8 + index * 6);
      });
      doc.line(
        10,
        startY + 8 + content.length * 6,
        200,
        startY + 8 + content.length * 6
      ); // Section underline
    };

    // Education
    addSection("Education", formData.education, 50);

    // Experience
    addSection("Experience", [formData.experience], 80);

    // Skills as a bullet point list
    addSection("Skills", formData.skills, 130);

    // Save the PDF
    doc.save(`${formData.name}_Resume.pdf`);
    setSuccess("Your resume has been generated!");

    // Reset the form fields
    setFormData(initialFormData);

    // Clear the success message after 2 seconds
    setTimeout(() => {
      setSuccess(""); // Clear success message
    }, 2000);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    generatePDF();
  };

  return (
    <Container
      className="mt-5"
      style={{ position: "relative", paddingBottom: "60px" }}
    >
      <h2>Create Your Resume</h2>
      {success && <Alert variant="success">{success}</Alert>}
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="formName" className="mb-3">
          <Form.Label>Name</Form.Label>
          <Form.Control
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            placeholder="Enter your name"
            required
          />
        </Form.Group>

        <Form.Group controlId="formEmail" className="mb-3">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="Enter your email"
            required
          />
        </Form.Group>

        <Form.Group controlId="formPhone" className="mb-3">
          <Form.Label>Phone</Form.Label>
          <Form.Control
            type="text"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            placeholder="Enter your phone number"
            required
          />
        </Form.Group>

        <Form.Label>Education</Form.Label>
        {formData.education.map((edu, index) => (
          <Form.Group
            controlId={`formEducation${index}`}
            className="mb-3"
            key={index}
          >
            <Form.Control
              type="text"
              value={edu}
              onChange={(e) => handleEducationChange(index, e.target.value)}
              placeholder="Enter your education"
              required
            />
          </Form.Group>
        ))}
        <div
          className="d-flex align-items-center mb-4"
          style={{ justifyContent: "flex-end" }}
        >
          <Button variant="primary" onClick={addEducation}>
            + Add Education
          </Button>
        </div>

        <Form.Group controlId="formExperience" className="mb-3">
          <Form.Label>Experience</Form.Label>
          <Form.Control
            as="textarea"
            name="experience"
            value={formData.experience}
            onChange={handleChange}
            placeholder="Enter your work experience"
            required
          />
        </Form.Group>

        <Form.Label>Skills</Form.Label>
        {formData.skills.map((skill, index) => (
          <Form.Group
            controlId={`formSkill${index}`}
            className="mb-3"
            key={index}
          >
            <Form.Control
              type="text"
              value={skill}
              onChange={(e) => handleSkillChange(index, e.target.value)}
              placeholder="Enter your skill"
              required
            />
          </Form.Group>
        ))}
        <div
          className="d-flex align-items-center mb-4"
          style={{ justifyContent: "flex-end" }}
        >
          <Button variant="primary" onClick={addSkill}>
            + Add Skill
          </Button>
        </div>

        {/* Positioning the button at the bottom left */}
        <div style={{ position: "absolute", bottom: "20px", left: "20px" }}>
          <Button variant="primary" type="submit">
            Generate Resume
          </Button>
        </div>
      </Form>
    </Container>
  );
}

export default ResumeBuilderPage;
