import React, { useState, useEffect } from 'react';
import axios from 'axios';

function IndustryNewsPage() {
    const [newsArticles, setNewsArticles] = useState([]); // Store the list of news articles
    const [selectedArticle, setSelectedArticle] = useState(null); // Store the currently selected article for details view
    const [comment, setComment] = useState(""); // Store the new comment input
    const [comments, setComments] = useState([]); // Store the comments for the selected article

    useEffect(() => {
        const fetchNews = async () => {
            const response = await axios.get('http://localhost:5000/api/news');
            setNewsArticles(response.data);
        };
        fetchNews();
    }, []);

    // Handle selecting a news article to show details
    const handleArticleClick = async (article) => {
        const response = await axios.get(`http://localhost:5000/api/news/${article.id}`);
        setSelectedArticle(response.data);
        setComments(response.data.comments); // Load the comments for the article
    };

    // Handle submitting a comment
    const handleCommentSubmit = async (e) => {
        e.preventDefault();
        const response = await axios.post(`http://localhost:5000/api/news/${selectedArticle.id}/comment`, { comment });
        setComments(response.data.article.comments); // Update the comments after submission
        setComment(""); // Clear the comment input
    };

    // Handle going back to the news list view
    const handleBackToList = () => {
        setSelectedArticle(null); // Deselect the article to return to the list
    };

    if (selectedArticle) {
        // If an article is selected, show the article details and comments
        return (
            <div className="container mt-5">
                <button onClick={handleBackToList} className="btn btn-secondary mb-3">
                    Back to News List
                </button>
                <h2>{selectedArticle.title}</h2>
                <img src={selectedArticle.image} alt={selectedArticle.title} className="img-fluid mb-4" />
                <p>{selectedArticle.content}</p>
                <p className="text-muted">{selectedArticle.datePosted}</p>

                <h4>Comments</h4>
                <ul>
                    {comments.map((cmt, idx) => (
                        <li key={idx}>{cmt}</li>
                    ))}
                </ul>

                <form onSubmit={handleCommentSubmit}>
                    <div className="mb-3">
                        <label htmlFor="comment" className="form-label">Leave a comment:</label>
                        <textarea
                            id="comment"
                            className="form-control"
                            value={comment}
                            onChange={(e) => setComment(e.target.value)}
                            rows="3"
                            required
                        ></textarea>
                    </div>
                    <button type="submit" className="btn btn-primary">Post Comment</button>
                </form>
            </div>
        );
    }

    // Otherwise, show the list of news articles
    return (
        <div className="container mt-5">
            <div className="row">
                {newsArticles.map(article => (
                    <div className="col-md-4 mb-4" key={article.id}>
                        <div className="card" onClick={() => handleArticleClick(article)}>
                            <img src={article.image} alt={article.title} className="card-img-top" />
                            <div className="card-body">
                                <h5 className="card-title">{article.title}</h5>
                                <p className="card-text">{article.summary}</p>
                                <p className="text-muted">{article.datePosted}</p>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default IndustryNewsPage;
