import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function IndustryNewsDetail() {
    const { id } = useParams();
    const [newsArticle, setNewsArticle] = useState(null);
    const [error, setError] = useState('');
    
    useEffect(() => {
        const fetchNewsArticle = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/news/${id}`);
                setNewsArticle(response.data);
            } catch (error) {
                setError('Error fetching the news article');
            }
        };

        fetchNewsArticle();
    }, [id]);

    if (error) {
        return <p>{error}</p>;
    }

    if (!newsArticle) {
        return <p>Loading...</p>;
    }

    return (
        <div>
            <h2>{newsArticle.title}</h2>
            <p>{newsArticle.content}</p>

            <h3>Comments</h3>
            <ul>
                {newsArticle.comments && newsArticle.comments.length > 0 ? (
                    newsArticle.comments.map((comment, index) => (
                        <li key={index}>
                            <strong>{comment.user}:</strong> {comment.text}
                        </li>
                    ))
                ) : (
                    <p>No comments yet.</p>
                )}
            </ul>
        </div>
    );
}

export default IndustryNewsDetail;
