import React from "react";
import { Row, Col, Button, Form } from "react-bootstrap";

function BookmarksPriority({ selectedCategory, onApplyFilter }) {
  const uniqueCategories = [
    "All",
    "Uncategorized",
    "High Priority",
    "Low Priority",
  ]; // Include "All"

  const handleFilterChange = (e) => {
    const selected = e.target.value;
    onApplyFilter(selected); // Apply filter to the parent component
  };

  return (
    <Row className="mb-4">
      <Col xs={12} className="d-flex justify-content-end align-items-center">
        <h3 className="me-2">Bookmarks</h3>
        <Form.Control
          as="select"
          value={selectedCategory} // Set the value from the selectedCategory prop
          onChange={handleFilterChange}
          className="me-2 w-25" // Make the dropdown wider
        >
          {uniqueCategories.map((category) => (
            <option key={category} value={category}>
              {category}
            </option>
          ))}
        </Form.Control>
        <Button
          variant="primary"
          onClick={() => onApplyFilter(selectedCategory)} // Use selectedCategory when applying the filter
        >
          Apply Filter
        </Button>
      </Col>
    </Row>
  );
}

export default BookmarksPriority;
