import React, { useState } from "react";

const ResumeBuilderPage = () => {
  const [formData, setFormData] = useState({
    education: [],
    experience: [],
    skills: [],
  });

  const [newEducation, setNewEducation] = useState("");
  const [newExperience, setNewExperience] = useState("");
  const [newSkill, setNewSkill] = useState("");

  const handleEducationChange = (e) => {
    setNewEducation(e.target.value);
  };

  const handleExperienceChange = (e) => {
    setNewExperience(e.target.value);
  };

  const handleSkillChange = (e) => {
    setNewSkill(e.target.value);
  };

  const handleAddEducation = () => {
    if (newEducation) {
      setFormData((prevData) => ({
        ...prevData,
        education: [...prevData.education, newEducation],
      }));
      setNewEducation(""); // Clear input field
    }
  };

  const handleAddExperience = () => {
    if (newExperience) {
      setFormData((prevData) => ({
        ...prevData,
        experience: [...prevData.experience, newExperience],
      }));
      setNewExperience(""); // Clear input field
    }
  };

  const handleAddSkill = () => {
    if (newSkill) {
      setFormData((prevData) => ({
        ...prevData,
        skills: [...prevData.skills, newSkill],
      }));
      setNewSkill(""); // Clear input field
    }
  };

  return (
    <div>
      <h1>Resume Builder</h1>

      {/* Education Section */}
      <div>
        <h2>Education</h2>
        <input
          type="text"
          value={newEducation}
          onChange={handleEducationChange}
          placeholder="Add Education"
        />
        <button onClick={handleAddEducation}>Add</button>
        <ul>
          {formData.education.map((edu, index) => (
            <li key={index}>{edu}</li>
          ))}
        </ul>
      </div>

      {/* Experience Section */}
      <div>
        <h2>Experience</h2>
        <input
          type="text"
          value={newExperience}
          onChange={handleExperienceChange}
          placeholder="Add Experience"
        />
        <button onClick={handleAddExperience}>Add</button>
        <ul>
          {formData.experience.map((exp, index) => (
            <li key={index}>{exp}</li>
          ))}
        </ul>
      </div>

      {/* Skills Section */}
      <div>
        <h2>Skills</h2>
        <input
          type="text"
          value={newSkill}
          onChange={handleSkillChange}
          placeholder="Add Skill"
        />
        <button onClick={handleAddSkill}>Add</button>
        <ul>
          {formData.skills.map((skill, index) => (
            <li key={index}>{skill}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ResumeBuilderPage;
