import React from "react";
import { Row, Col, Button, Form } from "react-bootstrap";

function BookmarksPriority({ categories, onApplyFilter }) {
  const uniqueCategories = ["Uncategorized", "High Priority", "Low Priority"];

  const handleFilterChange = (e) => {
    const selectedCategory = e.target.value;
    onApplyFilter(selectedCategory); // Apply filter to the parent component
  };

  return (
    <Row className="mb-4">
      <Col xs={12}>
        <h3>Filter Bookmarked Jobs by Priority</h3>
        <Form.Control
          as="select"
          onChange={handleFilterChange}
          className="me-2"
        >
          {uniqueCategories.map((category) => (
            <option key={category} value={category}>
              {category}
            </option>
          ))}
        </Form.Control>
        <Button
          variant="primary"
          onClick={() => onApplyFilter(selectedCategory)}
        >
          Apply Filter
        </Button>
      </Col>
    </Row>
  );
}

export default BookmarksPriority;
