import React, { useState } from 'react';
import axios from 'axios';

const SalaryEstimator = () => {
    const [role, setRole] = useState('');
    const [industry, setIndustry] = useState('');
    const [location, setLocation] = useState('');
    const [experience, setExperience] = useState('');
    const [salaryEstimate, setSalaryEstimate] = useState(null);
    const [error, setError] = useState('');

    const handleEstimate = async (e) => {
        e.preventDefault();
        setError(''); // Reset error message

        // Validate inputs
        if (!role || !industry || !location || !experience) {
            setError('All fields are required.');
            return;
        }

        try {
            const response = await axios.get('/api/salary/estimate', {
                params: { role, industry, location, experience }
            });
            setSalaryEstimate(response.data);
        } catch (err) {
            console.error(err);
            setError(err.response?.data?.message || 'Error estimating salary. Please try again.');
        }
    };

    const handleSaveEstimate = async () => {
        // Implement saving functionality here
        // This would typically involve sending the estimate to your backend
        console.log('Saving estimate:', salaryEstimate);
        // Show a success message or notification
    };

    return (
        <div>
            <h2>Salary Estimator</h2>
            <form onSubmit={handleEstimate}>
                <div>
                    <label htmlFor="role">Job Role:</label>
                    <input
                        type="text"
                        id="role"
                        value={role}
                        onChange={(e) => setRole(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="industry">Industry:</label>
                    <input
                        type="text"
                        id="industry"
                        value={industry}
                        onChange={(e) => setIndustry(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="location">Location:</label>
                    <input
                        type="text"
                        id="location"
                        value={location}
                        onChange={(e) => setLocation(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="experience">Years of Experience:</label>
                    <input
                        type="number"
                        id="experience"
                        value={experience}
                        onChange={(e) => setExperience(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Estimate Salary</button>
            </form>

            {error && <p style={{ color: 'red' }}>{error}</p>}

            {salaryEstimate && (
                <div>
                    <h3>Estimated Salary Ranges:</h3>
                    <ul>
                        {salaryEstimate.map((job, index) => (
                            <li key={index}>
                                <strong>{job.title}</strong> at {job.company} in {job.location}: {job.salary}
                            </li>
                        ))}
                    </ul>
                    <button onClick={handleSaveEstimate}>Save Estimate</button>
                </div>
            )}
        </div>
    );
};

export default SalaryEstimator;
