import React from "react";
import { Row, Col, Button, Form } from "react-bootstrap";

function BookmarksPriority({ selectedCategory, categories, onApplyFilter }) {
  const uniqueCategories = ["Uncategorized", "High Priority", "Low Priority"];

  return (
    <Row className="mb-4">
      <Col
        xs={12}
        className="d-flex justify-content-between align-items-center"
      >
        <h3>Bookmarks</h3>
        <div className="d-flex align-items-center">
          <Form.Control
            as="select"
            value={selectedCategory} // Set the value from the selectedCategory prop
            onChange={(e) => {
              // Update selected category on change
              const selected = e.target.value;
              onApplyFilter(selected); // Do not apply filter here
            }}
            className="me-2" // Margin to the right
            style={{ width: "250px" }} // Increased width for the dropdown
          >
            {uniqueCategories.map((category) => (
              <option key={category} value={category}>
                {category}
              </option>
            ))}
          </Form.Control>
          <Button
            variant="primary"
            onClick={() => {
              // Apply filter when button is clicked
              onApplyFilter(selectedCategory);
            }} // Use selectedCategory when applying the filter
          >
            Apply Filter
          </Button>
        </div>
      </Col>
    </Row>
  );
}

export default BookmarksPriority;
