// ResumeBuilderPage.js
import React, { useState } from "react";
import { Container, Form, Button, Alert } from "react-bootstrap";
import jsPDF from "jspdf";
import "jspdf-autotable"; // Import autoTable plugin for better table handling

function ResumeBuilderPage() {
  const initialFormData = {
    name: "",
    email: "",
    phone: "",
    careerGoal: "",
    experience: [],
    education: "",
    skills: "",
    interests: "",
  };

  const [formData, setFormData] = useState(initialFormData);
  const [success, setSuccess] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === "experience") {
      setFormData({
        ...formData,
        experience: value.split("\n"), // Split by line breaks for multi-line input
      });
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const generatePDF = () => {
    const doc = new jsPDF();

    // Add header
    doc.setFontSize(22);
    doc.setFont("helvetica", "bold");
    doc.text(formData.name, 14, 22); // Name
    doc.setFontSize(12);
    doc.setFont("helvetica", "normal");
    doc.text(formData.email, 14, 32); // Email
    doc.text(formData.phone, 14, 38); // Phone
    doc.text("LinkedIn: LinkedIn profile", 14, 44); // LinkedIn profile
    doc.line(10, 48, 200, 48); // Underline

    // Add career goals
    doc.setFontSize(14);
    doc.setFont("helvetica", "italic");
    doc.text("Career Goals", 14, 58);
    doc.setFont("helvetica", "normal");
    doc.text(formData.careerGoal, 14, 66, { maxWidth: 180 });

    // Add sections
    const addSection = (title, content, startY) => {
      doc.setFontSize(16);
      doc.setFont("helvetica", "bold");
      doc.text(title, 14, startY);
      doc.setFontSize(12);
      doc.setFont("helvetica", "normal");
      doc.text(content, 14, startY + 8, { maxWidth: 180 });
      doc.line(10, startY + 12, 200, startY + 12); // Section underline
    };

    // Experience
    doc.setFontSize(16);
    doc.setFont("helvetica", "bold");
    doc.text("Experience", 14, 90);
    doc.setFont("helvetica", "normal");
    formData.experience.forEach((exp, index) => {
      const yOffset = 98 + index * 30; // Adjust Y position for each experience
      doc.text(exp, 14, yOffset, { maxWidth: 180 });
    });

    // Education
    addSection(
      "Education",
      formData.education,
      98 + formData.experience.length * 30 + 10
    );

    // Skills
    addSection(
      "Skills",
      formData.skills,
      135 + formData.experience.length * 30
    );

    // Interests
    addSection(
      "Interests",
      formData.interests,
      150 + formData.experience.length * 30
    );

    // Save the PDF
    doc.save(`${formData.name}_Resume.pdf`);
    setSuccess("Your resume has been generated!");

    // Reset the form fields
    setFormData(initialFormData);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    generatePDF();
  };

  return (
    <Container className="mt-5">
      <h2>Create Your Resume</h2>
      {success && <Alert variant="success">{success}</Alert>}
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="formName" className="mb-3">
          <Form.Label>Name</Form.Label>
          <Form.Control
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            placeholder="Enter your name"
            required
          />
        </Form.Group>

        <Form.Group controlId="formEmail" className="mb-3">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            placeholder="Enter your email"
            required
          />
        </Form.Group>

        <Form.Group controlId="formPhone" className="mb-3">
          <Form.Label>Phone</Form.Label>
          <Form.Control
            type="text"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            placeholder="Enter your phone number"
            required
          />
        </Form.Group>

        <Form.Group controlId="formCareerGoal" className="mb-3">
          <Form.Label>Career Goals</Form.Label>
          <Form.Control
            as="textarea"
            name="careerGoal"
            value={formData.careerGoal}
            onChange={handleChange}
            placeholder="State your career goals..."
            required
          />
        </Form.Group>

        <Form.Group controlId="formExperience" className="mb-3">
          <Form.Label>Experience</Form.Label>
          <Form.Control
            as="textarea"
            name="experience"
            value={formData.experience.join("\n")}
            onChange={handleChange}
            placeholder="Enter your experience (one entry per line)"
            required
          />
        </Form.Group>

        <Form.Group controlId="formEducation" className="mb-3">
          <Form.Label>Education</Form.Label>
          <Form.Control
            as="textarea"
            name="education"
            value={formData.education}
            onChange={handleChange}
            placeholder="Enter your education details"
            required
          />
        </Form.Group>

        <Form.Group controlId="formSkills" className="mb-3">
          <Form.Label>Skills</Form.Label>
          <Form.Control
            as="textarea"
            name="skills"
            value={formData.skills}
            onChange={handleChange}
            placeholder="Enter your skills"
            required
          />
        </Form.Group>

        <Form.Group controlId="formInterests" className="mb-3">
          <Form.Label>Interests</Form.Label>
          <Form.Control
            as="textarea"
            name="interests"
            value={formData.interests}
            onChange={handleChange}
            placeholder="Enter your interests (optional)"
          />
        </Form.Group>

        <Button variant="primary" type="submit">
          Generate Resume
        </Button>
      </Form>
    </Container>
  );
}

export default ResumeBuilderPage;
