import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function IndustryNewsDetail() {
    const { id } = useParams();
    const [newsArticle, setNewsArticle] = useState(null);
    const [error, setError] = useState('');
    const [username, setUsername] = useState('');  // For user's name input
    const [commentText, setCommentText] = useState('');  // For comment text input
    const [comments, setComments] = useState([]);  // For storing the comments

    useEffect(() => {
        const fetchNewsArticle = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/news/${id}`);
                setNewsArticle(response.data);
                setComments(response.data.comments || []); // Set initial comments
            } catch (error) {
                setError('Error fetching the news article');
            }
        };

        fetchNewsArticle();
    }, [id]);

    // Handle comment submission
    const handleCommentSubmit = async (e) => {
        e.preventDefault();
        const newComment = {
            user: username,
            text: commentText,
            time: new Date().toLocaleString(), // Capture current time
        };

        // Update the comments state
        setComments((prevComments) => [newComment, ...prevComments]);  // Add newest comment to the top

        // Optionally send the new comment to the server (if back-end updates are required)
        try {
            await axios.post(`http://localhost:5000/api/news/${id}/comment`, newComment);
            setCommentText('');  // Clear the comment input
            setUsername('');     // Clear the username input
        } catch (error) {
            console.error('Error posting the comment:', error);
        }
    };

    if (error) {
        return <p>{error}</p>;
    }

    if (!newsArticle) {
        return <p>Loading...</p>;
    }

    return (
        <div className="container mt-5">
            <div className="row">
                <div className="col-md-8 offset-md-2">
                    <div className="mb-4">
                        <h1 className="display-5">{newsArticle.title}</h1>
                        <p className="text-muted">{newsArticle.datePosted}</p>
                        <p className="text-muted">By {newsArticle.author}</p>
                        <img src={newsArticle.image} alt={newsArticle.title} className="img-fluid mb-4 rounded" />
                    </div>
                    <div className="content mb-5">
                        <p className="lead">{newsArticle.content}</p>
                    </div>

                    
                    <div className="comment-section">
                        <h3>Leave a Comment</h3>
                        <form onSubmit={handleCommentSubmit}>
                            <div className="mb-3">
                                <label htmlFor="username" className="form-label">Your Name:</label>
                                <input
                                    type="text"
                                    id="username"
                                    className="form-control"
                                    value={username}
                                    onChange={(e) => setUsername(e.target.value)}
                                    required
                                />
                            </div>
                            <div className="mb-3">
                                <label htmlFor="comment" className="form-label">Comment:</label>
                                <textarea
                                    id="comment"
                                    className="form-control"
                                    value={commentText}
                                    onChange={(e) => setCommentText(e.target.value)}
                                    rows="3"
                                    required
                                ></textarea>
                            </div>
                            <button type="submit" className="btn btn-primary">Submit Comment</button>
                        </form>
                    </div>

                    {/* Comments Section */}
                    <div className="comments-section mt-5">
                        <h4>Comments</h4>
                        <ul className="list-unstyled">
                            {comments.length > 0 ? (
                                comments.map((comment, index) => (
                                    <li key={index} className="media mb-4">
                                        <div className="media-body">
                                            <h5 className="mt-0 mb-1">{comment.user}</h5>
                                            <p>{comment.text}</p>
                                            <p className="text-muted">{comment.time}</p> 
                                        </div>
                                    </li>
                                ))
                            ) : (
                                <p>No comments yet. Be the first to comment!</p>
                            )}
                        </ul>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default IndustryNewsDetail;
