import React, { useState, useEffect } from "react";
import {
  Container,
  Row,
  Col,
  Card,
  Button,
  Form,
  Modal,
} from "react-bootstrap";

function BookmarkPage() {
  const [bookmarkedJobs, setBookmarkedJobs] = useState([]);
  const [categories, setCategories] = useState({});
  const [selectedCategory, setSelectedCategory] = useState("Uncategorized");
  const [filteredJobs, setFilteredJobs] = useState([]);
  const [selectedJob, setSelectedJob] = useState(null); // State for modal
  const [showModal, setShowModal] = useState(false); // State for modal visibility

  useEffect(() => {
    // Fetch bookmarked jobs from local storage
    const storedBookmarks =
      JSON.parse(localStorage.getItem("bookmarkedJobs")) || [];
    setBookmarkedJobs(storedBookmarks);

    // Pre-assign some categories for testing
    const storedCategories = JSON.parse(
      localStorage.getItem("jobCategories")
    ) || {
      1: "High Priority", // Job with ID 1 is High Priority
      2: "Low Priority", // Job with ID 2 is Low Priority
    };
    setCategories(storedCategories);
    setFilteredJobs(storedBookmarks); // Initialize filtered jobs
  }, []);

  const removeBookmark = (jobId) => {
    const updatedBookmarks = bookmarkedJobs.filter((job) => job._id !== jobId);
    setBookmarkedJobs(updatedBookmarks);
    localStorage.setItem("bookmarkedJobs", JSON.stringify(updatedBookmarks));
  };

  const assignCategory = (jobId, category) => {
    const updatedCategories = { ...categories, [jobId]: category };
    setCategories(updatedCategories);
    localStorage.setItem("jobCategories", JSON.stringify(updatedCategories));
  };

  const applyFilter = () => {
    const jobsToDisplay =
      selectedCategory === "Uncategorized"
        ? bookmarkedJobs
        : bookmarkedJobs.filter(
            (job) => categories[job._id] === selectedCategory
          );
    setFilteredJobs(jobsToDisplay);
  };

  const handleShowModal = (job) => {
    setSelectedJob(job);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setSelectedJob(null);
  };

  return (
    <Container fluid className="p-4">
      <Row className="mb-4 align-items-center">
        <Col xs={12} md={6}>
          <h2>My Bookmarked Jobs</h2>
        </Col>
        <Col xs={12} md={6}>
          <div className="d-flex align-items-center">
            <Form.Control
              as="select"
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
              className="me-2"
            >
              <option value="Uncategorized">Uncategorized</option>
              <option value="High Priority">High Priority</option>
              <option value="Low Priority">Low Priority</option>
            </Form.Control>
            <Button variant="primary" onClick={applyFilter}>
              Apply Filter
            </Button>
          </div>
        </Col>
      </Row>

      <Row>
        {filteredJobs.length > 0 ? (
          filteredJobs.map((job) => (
            <Col xs={12} md={6} lg={4} key={job._id} className="mb-4">
              <Card className="job-card shadow-sm border-light position-relative">
                <Button
                  variant="danger"
                  onClick={() => removeBookmark(job._id)}
                  className="position-absolute top-0 end-0 m-2" // Positioned at the top right
                >
                  Remove Bookmark
                </Button>
                <Card.Body>
                  <Card.Title>{job.title}</Card.Title>
                  <Card.Subtitle className="mb-2 text-muted">
                    {job.company}
                  </Card.Subtitle>
                  <Card.Text>
                    <strong>Location:</strong> {job.location}
                    <br />
                    <strong>Salary:</strong> {job.salary}
                    <br />
                    <strong>Description:</strong>{" "}
                    {job.description.substring(0, 100)}...
                    <br />
                    <strong>Category:</strong>{" "}
                    {categories[job._id] || "Uncategorized"}
                  </Card.Text>

                  {/* Dropdown Menu above View Details */}
                  <Form.Control
                    as="select"
                    value={categories[job._id] || ""}
                    onChange={(e) => assignCategory(job._id, e.target.value)}
                    className="mb-2"
                  >
                    <option value="">Uncategorized</option>
                    <option value="High Priority">High Priority</option>
                    <option value="Low Priority">Low Priority</option>
                  </Form.Control>

                  <Button
                    variant="primary"
                    onClick={() => handleShowModal(job)} // Open modal on click
                    className="w-100" // Full width
                  >
                    View Details
                  </Button>
                </Card.Body>
              </Card>
            </Col>
          ))
        ) : (
          <Col xs={12} className="text-center">
            <p>No bookmarked jobs.</p>
          </Col>
        )}
      </Row>

      {/* Modal for Job Details */}
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>{selectedJob?.title}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <strong>Company:</strong> {selectedJob?.company}
          <br />
          <strong>Location:</strong> {selectedJob?.location}
          <br />
          <strong>Salary:</strong> {selectedJob?.salary}
          <br />
          <strong>Description:</strong> {selectedJob?.description}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={handleCloseModal}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </Container>
  );
}

export default BookmarkPage;
